mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM sbproduct


		WHERE productname LIKE '%'||#{search }||'%'
		AND kind = #{kindSelect }


		WHERE kind = #{kindSelect }


		WHERE productname LIKE '%'||#{search }||'%'

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, productname, kind, content, price, TO_CHAR(regdate, 'YYYY-MM-DD') as regdate, profile FROM sbproduct
WHERE productname LIKE '%'||#{keyword }||'%' AND kind = #{kindSelect }
WHERE productname LIKE '%'||#{keyword }||'%'
WHERE kind = #{kindSelect }
WHERE productname LIKE '%'||#{search }||'%'
ORDER BY num DESC
ORDER BY price DESC , num DESC
ORDER BY price ASC , num DESC
ORDER BY buycount DESC , num DESC
)result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}


SELECT productname
FROM sbproduct
WHERE productname=#{inputproductname}

mapper.update:
UPDATE sbproduct_sub SET
sbcount=#{sbcount}
WHERE num=#{num} and sbsize=#{sbsize}

update sblogin_account
set point = point + (#{totalPrice}*0.01)
where id = #{id}

update sblogin_account
set money = money - #{totalPrice}
where id = #{id}

update sbproduct_sub
set sbcount =  sbcount - #{sbcount}
where num = #{productnum} and sbsize = #{sbsize}
